package com.example.root.movieretrofitmvp.rest;import com.example.root.movieretrofitmvp.data.MovieResponse;import retrofit2.Call;import retrofit2.http.GET;import retrofit2.http.Query;public interface MovieInterface {    @GET("search/movie/")    Call<MovieResponse> getMovieByName(@Query("api_key") String API_KEY_PLUS_TITLE);    @GET("movie/now_playing/")    Call<MovieResponse> getNowPlayingMovie(@Query("api_key") String API_KEY);    @GET("movie/upcoming/")    Call<MovieResponse> getUpcomingMovie(@Query("api_key") String API_KEY);}