package com.example.root.movieretrofitmvp.view;import android.os.PersistableBundle;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.widget.ImageView;import android.widget.TextView;import com.example.root.movieretrofitmvp.BuildConfig;import com.example.root.movieretrofitmvp.R;import com.example.root.movieretrofitmvp.data.MovieModel;import com.squareup.picasso.Picasso;public class DetailActivity extends AppCompatActivity {    public static final String MOVIE_DATA = "movie_data";    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_detail);        ImageView backdrop = (ImageView) findViewById(R.id.detailBackdrop);        TextView detail_title = (TextView) findViewById(R.id.detailTitle);        TextView detail_release = (TextView) findViewById(R.id.detailRelease);        TextView detail_overview = (TextView) findViewById(R.id.detailOverview);        MovieModel movie = getIntent().getParcelableExtra(MOVIE_DATA);        detail_title.setText(movie.getTitle());        detail_release.setText(movie.getOverview());        detail_overview.setText(movie.getRelease());        Picasso.with(this)                .load(BuildConfig.BACKDROP_PATH+movie.getBackdrop_path())                .placeholder(this.getResources().getDrawable(R.mipmap.ic_launcher))                .error(this.getResources().getDrawable(R.mipmap.ic_launcher))                .into(backdrop);    }    @Override    public void onSaveInstanceState(Bundle outState) {        super.onSaveInstanceState(outState);    }}