package com.example.root.movieretrofitmvp.view;import android.app.ProgressDialog;import android.os.Build;import android.support.annotation.NonNull;import android.support.annotation.RequiresApi;import android.support.v4.widget.SwipeRefreshLayout;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.Toast;import com.example.root.movieretrofitmvp.BuildConfig;import com.example.root.movieretrofitmvp.R;import com.example.root.movieretrofitmvp.adapter.MovieAdapter;import com.example.root.movieretrofitmvp.data.MovieModel;import com.example.root.movieretrofitmvp.data.MovieResponse;import com.example.root.movieretrofitmvp.rest.MovieClients;import com.example.root.movieretrofitmvp.rest.MovieInterface;import java.util.List;import java.util.Objects;import retrofit2.Call;import retrofit2.Callback;import retrofit2.Response;public class MainActivity extends AppCompatActivity {    private static String API_KEY = BuildConfig.API_KEY;    private static String TAG_MOVIE = MainActivity.class.getSimpleName();    EditText search;    Button btnSearch;    RecyclerView recyclerView;    SwipeRefreshLayout swipeRefreshLayout;    MovieAdapter adapter;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        search = (EditText) findViewById(R.id.searchTitle);        btnSearch = (Button)findViewById(R.id.btnSearch);        recyclerView = (RecyclerView)findViewById(R.id.recyclerview);        swipeRefreshLayout = (SwipeRefreshLayout)findViewById(R.id.refresh);        recyclerView.setLayoutManager(new LinearLayoutManager(this));        swipeRefreshLayout.setOnRefreshListener(new SwipeRefreshLayout.OnRefreshListener() {            @Override            public void onRefresh() {                getData();            }        });        btnSearch.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                getData();            }        });    }    public  void getData(){        final ProgressDialog dialog = new ProgressDialog(this);        dialog.setMessage("Loading... Getting Movie...");        dialog.show();        String judul = search.getText().toString();        String url = API_KEY+"&language=en-US&query="+judul;        MovieInterface movieInterface = MovieClients.getMovieClients().create(MovieInterface.class);        Call<MovieResponse> call = movieInterface.getMovieByName(url);        call.enqueue(new Callback<MovieResponse>() {            @Override            public void onResponse(@NonNull Call<MovieResponse> call, @NonNull Response<MovieResponse> response) {                List<MovieModel> movieModels = response.body().getResults();                recyclerView.setAdapter(new MovieAdapter(getApplicationContext(), movieModels));                dialog.dismiss();            }            @Override            public void onFailure(@NonNull Call<MovieResponse> call, @NonNull Throwable t) {                Toast.makeText(getApplicationContext(), "Your Search Movie Not Getting Results", Toast.LENGTH_LONG).show();                dialog.dismiss();            }        });    }    @Override    public void onSaveInstanceState(Bundle outState) {        super.onSaveInstanceState(outState);    }}